import './App.css';
import React, {useState} from 'react';
import Axios from 'axios';
import {
  Button,
  Form,
  FormGroup,
  Input,
  Label
} from 'reactstrap';

function Register() {
  
  const [nameReq, setNameReq] = useState('')
  const [emailReq, setEmailReq] = useState('')
  const [phoneReq, setPhoneReq] = useState('')
  const [addressReq, setAddressReq] = useState('')
  const [passwordReq, setPasswordReq] = useState('')
  const [registerStatus, setRegisterStatus] = useState('')
  const register = () => {
    console.log("in");
    Axios.post("http://localhost:3001/register", 
    {
      name:nameReq, 
      email:emailReq, 
      phone:phoneReq, 
      address:addressReq, 
      password:passwordReq 
    }).then((response) => {
      if (response.data.message){
        setRegisterStatus(response.data.message);
      }
      else{
        setRegisterStatus(response.data[0].name);
      }
    });
  };

  return (
    <body className="bg-dark" >
    <div className="App">
        <h2 className="text-warning text-center">Register</h2>
        <Form className="form">
          <FormGroup>
            <Label className="text-white" for="exampleEmail">Name-Surname</Label>
            <Input
              type="name"
              name="name"
              id="name"
              onChange={(e) => {setNameReq(e.target.value);}}
            />
          </FormGroup>
          <FormGroup>
            <Label className="text-white" for="exampleEmail">Email</Label>
            <Input
              type="email"
              name="email"
              id="exampleEmail"
              onChange={(e) => {setEmailReq(e.target.value);}}
            />
          </FormGroup>
          <FormGroup>
            <Label className="text-white" for="exampleEmail">Phone Number</Label>
            <Input
              type="phone"
              name="phone"
              id="phone"
              onChange={(e) => {setPhoneReq(e.target.value);}}
            />
          </FormGroup>
          <FormGroup>
            <Label className="text-white" for="exampleEmail">Address</Label>
            <Input
              type="address"
              name="address"
              id="address"
              onChange={(e) => {setAddressReq(e.target.value);}}
            />
          </FormGroup>
          <FormGroup>
            <Label className="text-white" for="examplePassword">Password</Label>
            <Input
              type="password"
              name="password"
              id="examplePassword"
              onChange={(e) => {setPasswordReq(e.target.value);}}
            />
          </FormGroup>
        <Button onClick={register}>Submit</Button>
        <h1>{registerStatus}</h1>
      </Form>
    </div>
    </body>
  );
}

export default Register;
