import './App.css';
import React, {useState} from 'react';
import { useHistory } from "react-router-dom";
import Axios from 'axios';
import {
  Button,
  Card,
  CardBody,
  Form,
  FormGroup,
  Input,
  Label
} from 'reactstrap';

function validateInfo(values){
  let errors ={}
  if (!values.name.trim()){
    errors.name = "Name required"
  }
  if (!values.email.trim()){
    errors.email = "Email required"
  }
  else if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(values.email)){
    errors.email = "Email address is invalid"
  }

  if (!values.password){
    errors.password = "Password is required"
  }
  else if (!values.password.length<6){
    errors.password = "Password needs to be 6 characters or more."
  }

  if (!values.password2){
    errors.password = "Password is required"
  }
  else if (values.password2 !== values.password){
    errors.password = "Passwords do not match"
  }
  return errors;
}

function Register() {
 
  const [nameReq, setNameReq] = useState('')
  const [emailReq, setEmailReq] = useState('')
  const [phoneReq, setPhoneReq] = useState('')
  const [addressReq, setAddressReq] = useState('')
  const [passwordReq, setPasswordReq] = useState('')
  const [registerStatus, setRegisterStatus] = useState('')
  
  const [values, setValues] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    password: '',
    password2: '',

  });

  const [errors, setErrors] = useState('');
 
  const handleChange = e => {
    const {name, value} = e.target;
    setValues({
      ...values,
      [name] : value
    });
  };
  const handleSubmit = e => {
    e.preventDefault();
    setErrors(validateInfo(values))
  }


  const register = () => {
    console.log("in");
    Axios.post("http://localhost:3001/register", 
    {
      name:nameReq, 
      email:emailReq, 
      phone:phoneReq, 
      address:addressReq, 
      password:passwordReq 
    }).then((response) => {
      if (response.data.message){
        setRegisterStatus(response.data.message);
      }
      else{
        setRegisterStatus(response.data[0].name);
        history.push("/login");
      }
    });
  };


  const history = useHistory();
  
  return (
    <Card style={{height:"100%"}} className="bg-dark">
    <div className="App">
        <Form className="form" onSubmit={handleSubmit}>
        <h2 className="text-warning text-center">Register</h2>
          <FormGroup>
            <Label className="text-white">Name-Surname</Label>
            <Input
              type="name"
              name="name"
              id="name"
              onChange={handleChange}
            />
            {errors.name && <p>{errors.name}</p>}
          </FormGroup>
          <FormGroup>
            <Label className="text-white">Email</Label>
            <Input
              type="email"
              name="email"
              id="exampleEmail"
              onChange={handleChange}
            />
            {errors.email && <p>{errors.email}</p>}
          </FormGroup>
          <FormGroup>
            <Label className="text-white">Phone Number</Label>
            <Input
              type="phone"
              name="phone"
              id="phone"
              onChange={handleChange}
            />
            {errors.phone && <p>{errors.phone}</p>}
          </FormGroup>
          <FormGroup>
            <Label className="text-white">Address</Label>
            <Input
              type="address"
              name="address"
              id="address"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <Label className="text-white">Password</Label>
            <Input
              type="password"
              name="password"
              id="examplePassword"
              onChange={handleChange}
            />
            {errors.password && <p>{errors.password}</p>}
          </FormGroup>
          <FormGroup>
            <Label className="text-white">Password</Label>
            <Input
              type="password"
              id="examplePassword"
              onChange={handleChange}
            />
            {errors.password2 && <p>{errors.password2}</p>}
          </FormGroup>
        <Button onClick={register}>Submit</Button>
        <h1>{registerStatus}</h1>
      </Form>
    </div>
    </Card>
    
  );
}

export default Register;
